import React from 'react'
import { useState, useEffect } from 'react'
import TokenContext from '../contexts/Token'
import { useContext } from 'react'
import { Navigate, useNavigate } from 'react-router-dom'
import Moment from 'react-moment';

const Tasks = () => {
    const navigate = useNavigate()
    let[tasks, setTasks] = useState([])
    const[formData, setFormData] = useState({
        task: "",
        amount: "",
        })
    const[editing, setEditing] = useState(false)
    const[selectedTask, setSelectedTask] = useState()

    const{ token, setUser, setToken, user } = useContext(TokenContext)

    let getTasks = async () => {
        let response = await fetch("http://127.0.0.1:8000/api/get-tasks/", {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + String(token.access)
            }
        })
        let data = await response.json()
        if(response.status === 200) {
            setTasks(data)
        } else {
            setUser(null)
            setToken(null)
            localStorage.removeItem("userName")
            localStorage.removeItem("userEmail");
            navigate("/login")
        }
    }

    let addTask = async () => {
        let response = await fetch("http://127.0.0.1:8000/api/add-task/", {
            method: "POST",
            headers:{
                "Content-Type": "application/json",
                "Authorization": "Bearer " + String(token.access)
            },
            body: JSON.stringify({
                "task": formData.task,
                "amount": formData.amount,
                // "user": user,
            })
        })
        let data = await response.json()
        if(response.status === 200) {
            setFormData({
                task: "",
                amount: "",
            })
            getTasks()
        }
    }

    let deleteTask = async (e) => {
        let taskDelete = e.target.value
        let response = await fetch(`http://127.0.0.1:8000/api/edit-task/${taskDelete}`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + token.access
            }
        })
        let data = await response.json()
        getTasks()
    }

    let editTask = async(e) => {
        e.preventDefault();
        console.log(`ID: ${e.target.edit.value}`)
        console.log(`Task: ${e.target.task.value}`)
        console.log(`Amount: ${e.target.amount.value}`)
        let taskEdit = e.target.value
        // let response = await fetch(`http://127.0.0.1:8000/api/edit-task/${taskEdit}`, {
        //     method: "PUT",
        //     headers: {
        //         "Content-Type": "application/json",
        //         "Authorization": "Bearer " + token.access
        //     }
        // })
        // let data = await response.json()
        // getTasks()
    }

    // HANDLE FUNCTIONS

    function handleSubmit(e) {
        e.preventDefault();
        // alert(`task: ${formData.task}, amount: ${formData.amount}`)
        addTask()
    }

    function handleChange(event) {
        const {name, value} = event.target;
        setFormData((prevFormData) => ({
            ...prevFormData,
            [name]: value,
        }))
        }

    function handleDelete(e) {
        e.preventDefault()

        deleteTask(e)
    }

    function handleEditButton(e) {
        e.preventDefault()
        setEditing(true)
        setSelectedTask(e.target.value)
        console.log(selectedTask)
    }

    useEffect(() => {
        getTasks()
    }, [])

    function editingTask(task) {
        return (
            <form onSubmit={editTask}>
                <input 
                    type="text" 
                    name="task"
                    value={formData.task}
                    onChange={handleChange}
                    placeholder={task.task}
                />
                <input 
                    type="number" 
                    name="amount"
                    step="0.10"
                    value={formData.amount}
                    onChange={handleChange}
                    required
                    placeholder={task.amount}
                />
                <button type="submit" value={task.id} name="edit">Submit Edit</button>
            </form>    
        )}


    const taskList = () => {
        // let editingMode = false
        // if(editing === true && )
        return (
            <ul>
                {tasks.map(task => (
                    <li key={task.id}>
                        {editing === false ? <p>{task.task} : £{task.amount}
                            <Moment format="hh:mm : DD/MM/YY" withTitle date={task.created}/></p> 
                            : editingTask(task)}
                        <button name="edit" value={task.id} onClick={e => handleEditButton(e)}>Edit</button>
                        <button name="delete" value={task.id} onClick={e => handleDelete(e)}>Delete</button>
                    </li>
                ))}
            </ul>
        )
    }

    return (
        <div>
            <div className='portal-addtask'>
            <h1>Add Task Here</h1>
            <form onSubmit={handleSubmit}>
            <input 
                type="text" 
                name="task"
                value={formData.task}
                onChange={handleChange}
                required
                placeholder='Task'
            />
            <input 
                type="number" 
                name="amount"
                step="0.10"
                value={formData.amount}
                onChange={handleChange}
                required
                placeholder='Amount(£0.00)'
            />
            <button type="submit">Add Task</button>
        </form>
        </div>
        <div className="portal-tasklist">
            <h1>Tasks</h1>
            {taskList()}
        </div>
        </div>
    )
}

export default Tasks
