import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBars } from '@fortawesome/free-solid-svg-icons'

const Navbar = (props) => {
    const[hideLinks, setHideLinks] = React.useState("show");

    React.useEffect(() => {

        // Hide or show Nav Links
        function handleResize() {
            if(window.innerWidth < 550) {
                setHideLinks("hide")
            } else {
                setHideLinks("show")
            }
        }

        // Listen for window size change
        window.addEventListener("resize", handleResize);
    }, [])

    const navLinks = (
        <ul className="nav-list">
            <li onClick={() => props.handlePage("register")}>Register</li>
            <li onClick={() => props.handlePage("login")}>Login</li>
            <li onClick={() => props.handlePage("dashboard")}>Dashboard</li>
            <li onClick={() => props.handlePage("parent-portal")}>Parent Portal</li>
        </ul>
    )

    return (
        <nav className="nav-bar">
            {hideLinks === "hide" && <FontAwesomeIcon icon={faBars} size="2xl" className="icon-fa-bar" />}
            {hideLinks === "show" && navLinks}
            <ul className={hideLinks=== "show" ? "nav-list" : "nav-list-hidden"}>
                <li onClick={() => props.handlePage("register")}>Register</li>
                <li onClick={() => props.handlePage("login")}>Login</li>
                <li onClick={() => props.handlePage("dashboard")}>Dashboard</li>
                <li onClick={() => props.handlePage("parent-portal")}>Parent Portal</li>
            </ul>
        </nav>
    )
}

export default Navbar
