import React from 'react'
import { useEffect, useState } from 'react'
import { useContext } from 'react';
import TokenContext from '../../contexts/Token';

export default function ChildList() {
    const[children, setChildren] = useState();
    const{ childId, setChildId} = useContext(TokenContext)
    const{clicked, setClicked} = useState(false)

    let getChildren = async() => {
        let response = await fetch(`http://127.0.0.1:8000/api/get-children/`, {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + localStorage.getItem("token"),
            }
        })
        let data = await response.json()
        if(response.status === 200) {
            setChildren(data)
        }
    }

    useEffect(() => {
        getChildren()
    }, [])



    return (
        <div>
            <div className='grid '>
                <ul className='flex w-[320px] place-content-center'>
                    {children && children.map(child => (
                        <li className={clicked ? 
                            "mx-2 cursor-pointer text-4xl text-slate-100 hover:underline hover:text-yellow-300" : "mx-2 cursor-pointer text-4xl text-slate-100 hover:underline hover:text-red-500"}

                            key={child.id} onClick={() => setChildId(child.id)}>{child.name}</li>
                    ))}
                </ul>
            </div>
        </div>
    )
}
