import React from 'react'
import { useEffect, useState } from 'react'

export default function TotalSaved() {
    const[totalSaved, setTotalSaved] = useState("Loading")

    let getTotalSaved = async() => {
        let response = await fetch("http://127.0.0.1:8000/api/get-total/", {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + String(localStorage.getItem("token"))
            }
        })
        let data = await response.json
    }

    useEffect(() => {
        getTotalSaved()
    }, [])



    return (
        <div className='grid grid-cols-3 grid-rows-3 gap-4 place-content-center'>
        <div className='col-span-3 place-self-center mt-10'>
            <h3 className=' text-6xl '>You have saved...</h3>
        </div>
        <div className='row-start-2 col-start-2 place-self-center '>
                <h1 className='m-0 text-6xl'>{totalSaved}</h1>
            </div>
        </div>
    )
}
