import React from 'react'
import { useState } from 'react'
import { Popconfirm } from 'antd'
import { useContext } from 'react'
import TokenContext from '../../contexts/Token'

const CompleteTask = (props) => {
    const[completeID, setCompleteID] = useState()
    const{token} = useContext(TokenContext)

    function handleComplete(e) {
        alert(completeID)
        let completeTask = async ()=>{
            let response = await fetch(`http://127.0.0.1:8000/api/edit-task/${completeID}/`, {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": "Bearer " + token.access,
                },
                body: JSON.stringify({
                    "id": e.target.id.value,
                    "task": e.target.task.value,
                    "amount": e.target.amount.value,
                    "created": e.target.created.value,
                    "complete": e.target.complete.value,
                    "user": e.target.user.value,
                }) 
            })
            let daya = await response.json()
        }
        completeTask()
    }

    return (
        <>
            <Popconfirm title="Are you sure you want to complete this task?" onConfirm={handleComplete}>
                <button 
                    type="submit" 
                    name="delete" 
                    value={props.taskID} 
                    onClick={()=>setCompleteID(props.taskID)}
                    className='button bg-blue'
                    >Complete
                </button>
            </Popconfirm>
        </>
    )
}

export default CompleteTask
