import React from 'react'

const Register = () => {
  // Init State for Registration Data
  const[formData, setFormData] = React.useState({
    parentName: "",
    parentEmail: "",
    parentPassword: "",
    childName: "",
    childUsername: "",
    childPassword: "",
  })

  const[formSize, setFormSize] = React.useState("rf-form")

  React.useEffect(() => {

    // Hide or show Nav Links
    function handleResize() {
        if(window.innerWidth < 940) {
          setFormSize("rf-form-flex-column")
        } else {
          setFormSize("rf-form")
        }
    }

    // Listen for window size change
    window.addEventListener("resize", handleResize);
    handleResize()
}, [])

  // Update registerData whenever form data changes
  function handleChange(event) {
    const {name, value} = event.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }))
  }

  function handleSubmit(event) {
    event.preventDefault();
    alert("You have Registered!")
    console.log(formData)

    // FETCH POST

  }

  return (
    <form className="rf-form" onSubmit={handleSubmit}>

          <div className="rf-parent-input">
            <h2 className="rf-parent-heading">Parent Sign Up</h2>
            <input 
              placeholder="Parent Name"
              className="rf-input rf-parent-name"
              type="text"
              id="parent-name"
              name="parentName"
              value={formData.parentName}
              onChange={handleChange}/>
            <input 
              placeholder="Parent Email"
              className="rf-input rf-parent-email"
              type="email"
              id="parent-email"
              name="parentEmail"
              value={formData.parentEmail}
              onChange={handleChange}/>
            <input 
              placeholder="Parent Password"
              className="rf-input rf-parent-password"
              type="password"
              id="parent-password"
              name="parentPassword"
              value={formData.parentPassword}
              onChange={handleChange}/>
            <input 
              placeholder="Confirm Password"
              className="rf-input rf-parent-confirm-password"
              type="password"/>
          </div>

          <div className="rf-child-input">
            <h2 className="rf-child-heading">Child Details</h2>
            <input 
              placeholder="Child Name"
              className="rf-input rf-child-name"
              type="text"
              id="child-name"
              name="childName"
              value={formData.childName}
              onChange={handleChange}/>
            <input 
              placeholder="Child Username"
              className="rf-input rf-child-username"
              type="text"
              id="child-username"
              name="childUsername"
              value={formData.childUsername}
              onChange={handleChange}/>
            <input 
              placeholder="Child Password"
              className="rf-input rf-child-password"
              type="password"
              id="child-password"
              name="childPassword"
              value={formData.childPassword}
              onChange={handleChange}/>
            <input 
              placeholder="Confirm Password"
              className="rf-input rf-child-confirm-password"
              type="password"/>
          </div>

        <button className='rf-submit'>Register</button>

    </form>
  )
}

export default Register
