import React from 'react'
import { useState } from 'react'
import { Popconfirm } from 'antd'
import { useContext } from 'react'
import TokenContext from '../../contexts/Token'

const CompleteTask = ({task}) => {
    const[completeID, setCompleteID] = useState()
    const{token} = useContext(TokenContext)

    function handleComplete(e) {
        let completeTask = async (e)=>{
            let response = await fetch(`http://127.0.0.1:8000/api/edit-task/${completeID}/`, {
                method: "PUT",
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": "Bearer " + token.access,
                },
                body: JSON.stringify({
                    "id": completeID,
                    "task": task.task,
                    "amount": task.amount,
                    "created": task.created,
                    "complete": task.complete,
                    "user": task.user,
                }) 
            })
            let daya = await response.json()
        }
        completeTask(e)
    }

    return (
        <>
            <Popconfirm title="Are you sure you want to complete this task?" onConfirm={handleComplete}>
                <button 
                    type="submit" 
                    name="delete" 
                    value={task.id} 
                    onClick={()=>setCompleteID(task.id)}
                    className='button bg-blue'
                    >Complete
                </button>
            </Popconfirm>
        </>
    )
}

export default CompleteTask
