import React from 'react';
import './App.css';

import Dashboard from'./components/Dashboard';
import Login from './components/Login';
import Navbar from './components/Navbar';
import ParentPortal from './components/ParentPortal';
import Register from './components/Register';

function App() {
  const[pageSelector, setPageSelector] = React.useState("login");
  const[page, setPage] = React.useState(<Login />);
  const[loggedOut, setLoggedOut] = React.useState("")
  const[token, setToken] = React.useState("nothing")

  React.useEffect(() => {
    if(pageSelector === "login") {
      setPage(<Login 
                setPageSelector={setPageSelector}
                loggedOut={loggedOut}
                token={token}/>)
    } else if(pageSelector === "register") {
      setPage(<Register setPageSelector={setPageSelector}/>)
    } else if(pageSelector === "dashboard") {
      setPage(<Dashboard />)
    } else if(pageSelector === "parent-portal") {
      setPage(<ParentPortal />)
    }
  }, [pageSelector])

  return (
    <>
      <Navbar 
        handlePage={e => setPageSelector(e)}
        setLoggedOut={e => setLoggedOut(e)}
        token={token}/>
      <div className="main-page">
        {page}
      </div>
    </>
  );
}

export default App;
