import React from 'react'

const Popup = ({isOpen, close, children}) => {

    if(!isOpen) return null

    const POP_BOX = {
        position: "absolute",
        top: "100px",
        display: "inline-block",
        width: "200px",
        height: "300px",
        border: "solid",
        textAlign: "center",
        fontSize: "50px",
        zIndex: "1000",
    }

    return (
        <div style={POP_BOX}>
        {children}
        <button onClick={close}>Close</button>
        </div>
    )
}

export default Popup
