import React from 'react'
import { useEffect, useState } from 'react'

export default function ChildList() {
    const[children, setChildren] = useState();
    const[childId, setChildId] = useState();
    const[selectedChild, setSelectedChild] = useState();

    let getChildren = async() => {
        let response = await fetch(`http://127.0.0.1:8000/api/get-children/`, {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + localStorage.getItem("token"),
            }
        })
        let data = await response.json()
        if(response.status === 200) {
            setChildren(data)
            console.log(data)
        }
    }

    let getChildTasks = async() => {
        let response = await fetch("http://127.0.0.1:8000/api/get-tasks/", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + localStorage.getItem("token"),
            },
            body: JSON.stringify({
                "childID": childId,
            })
        })
    }

    useEffect(() => {
        getChildren()
        setChildId(children[0].childId)
    }, [])

    useEffect(() => {
        getChildTasks()
    }, [childId])

    return (
        <div>
        <h3>Children</h3>
        <ul>
            {children && children.map(child => (
                <li key={child.id} onClick={() => setChildId(child.id)}>{child.name}</li>
            ))}
        </ul>
        </div>
    )
}
