  const{user} = useContext(TokenContext)
  const[parentLogged, setParentLogged] = React.useState(localStorage.getItem("user_name"))
  console.log(user)
  return (
    <div className="pp-main-page">
      { user === null ? <Navigate to="/login"/> : 
      <h1>Hello, {parentLogged}!</h1> }
      <br />
      <AddTask />
    </div>
  )
}

export default ParentPortal
