import React from 'react'
import { useState, useEffect } from 'react'
import TokenContext from '../contexts/Token'
import { useContext } from 'react'
import { Navigate, useNavigate } from 'react-router-dom'

const Tasks = () => {
    const navigate = useNavigate()
    let[tasks, setTasks] = useState([])
    const[formData, setFormData] = useState({
        task: "",
        amount: "",
        })

    const{ token, setUser, setToken, user } = useContext(TokenContext)

    let getTasks = async () => {
        let response = await fetch("http://127.0.0.1:8000/api/get-tasks/", {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + String(token.access)
            }
        })
        let data = await response.json()
        if(response.status === 200) {
            setTasks(data)
        } else {
            setUser(null)
            setToken(null)
            localStorage.removeItem("userName")
            localStorage.removeItem("userEmail");
            navigate("/login")
        }
    }

    let addTask = async () => {
        let response = await fetch("http://127.0.0.1:8000/api/add-task/", {
            method: "POST",
            headers:{
                "Content-Type": "application/json",
                "Authorization": "Bearer " + String(token.access)
            },
            body: JSON.stringify({
                "task": formData.task,
                "amount": formData.amount,
                // "user": user,
            })
        })
        let data = await response.json()
        if(response.status === 200) {
            alert("Task Added!")
            getTasks()
        }
    }

    function handleSubmit(e) {
        e.preventDefault();
        // alert(`task: ${formData.task}, amount: ${formData.amount}`)
        addTask()
    }

    function handleChange(event) {
        const {name, value} = event.target;
        setFormData((prevFormData) => ({
            ...prevFormData,
            [name]: value,
        }))
        }

    useEffect(() => {
        getTasks()
    }, [])

    return (
        <div>
            <h1>Add Task Here</h1>
            <form onSubmit={handleSubmit}>
            <input 
                type="text" 
                name="task"
                value={formData.task}
                onChange={handleChange}
                required
                placeholder='Task'
            />
            <input 
                type="number" 
                name="amount"
                step="0.10"
                value={formData.amount}
                onChange={handleChange}
                required
                placeholder='Amount(Â£0.00)'
            />
            <button type="submit">Add Task</button>
        </form>
        <div className="className=portal-tasklist">
            <h1>Tasks</h1>
            <ul>
                {tasks.map(task => (
                    <li key={task.id}>{task.task} : {task.amount}</li>
                ))}
            </ul>
        </div>
        </div>
    )
}

export default Tasks
