import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBars } from '@fortawesome/free-solid-svg-icons'

const Navbar = (props) => {
    const[hideLinks, setHideLinks] = React.useState("show");

    React.useEffect(() => {

        function handleResize() {
            if(window.innerWidth < 550) {
                alert("Too small")
                setHideLinks("hide")
            }
        }

        window.addEventListener("resize", handleResize);
    }, [])

    const navLinks = (
        <ul className="nav-list">
            <li onClick={() => props.handlePage("register")}>Register</li>
            <li onClick={() => props.handlePage("login")}>Login</li>
            <li onClick={() => props.handlePage("dashboard")}>Dashboard</li>
            <li onClick={() => props.handlePage("parent-portal")}>Parent Portal</li>
        </ul>
    )

    return (
        <nav className="nav-bar">
            <FontAwesomeIcon icon={faBars} size="2xl" className="icon-fa-bar" />
            {hideLinks === "show" && navLinks}
        </nav>
    )
}

export default Navbar
