    // //         },
    // //         body: JSON.stringify({
    // //             "childID": childId,
    // //         })
    // //     })
    // // }

    useEffect(() => {
        getChildren()
    }, [])



    return (
        <div>
        <h3>Children</h3>
        <ul>
            {children && children.map(child => (
                <li key={child.id} onClick={() => setChildId(child.id)}>{child.name}</li>
            ))}
        </ul>
        </div>
    )
}
