import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBars } from '@fortawesome/free-solid-svg-icons'
import MenuListData from "./MenuListData";
import Sidebar from "./Sidebar";

const Navbar = (props) => {
    const[hideLinks, setHideLinks] = React.useState("show");

    React.useEffect(() => {

        // Hide or show Nav Links
        function handleResize() {
            if(window.innerWidth < 900) {
                setHideLinks("hide")
            } else {
                setHideLinks("show")
            }
        }

        // Listen for window size change
        window.addEventListener("resize", handleResize);
    }, [])

    return (
        <nav className="nav-bar">
            <FontAwesomeIcon icon={faBars} size="2xl" className={hideLinks === "hide" ? "icon-fa-bar" : "icon-fa-bar-hidden"} />
            <h1 className={hideLinks ==="hide" ? "saving-logo-center" : "saving-logo"}>Saving Stars</h1>
            <MenuListData className={hideLinks=== "show" ? "nav-list" : "nav-list-hidden"}/>
            <Sidebar />
        </nav>
    )
}

export default Navbar
